mapper.insert:
insert into book_info (`publisher_id`, `publish_place`, `isbn`, `cip`, `book_name`,
                       `book_type`, `cover`, `author`, `describe`, `publish_date`,
                       `keyword`, `lang`, `price`, `stock`, `created_by`, `creation_time`,
                       `updated_by`, `last_updated_time`, `revision`)
values ( #{entity.publisher.id}
       , #{entity.publishPlace}
       , #{entity.isbn}
       , #{entity.cip}
       , #{entity.bookName}
       , #{entity.bookType}
       , #{entity.cover}
       , #{entity.author}
       , #{entity.describe}
       , #{entity.publishDate}
       , #{entity.keyword}
       , #{entity.lang}
       , #{entity.price}
       , #{entity.stock}
       , #{entity.createdBy}
       , NOW()
       , #{entity.updatedBy}
       , now()
       , 0)

mapper.delete:
delete bif
from book_info bif,
     authority a
where bif.`id` = #{id}

mapper.select:
select distinct bif.`id`, bif.`publish_id`, bif.`publish_place`, bif.`isbn`, bif.`cip`, bif.`book_name`, bif.`book_type`, bif.`cover`, bif.`author`, bif.`describe`, bif.`publish_date`, bif.`keyword`, bif.`lang`, bif.`price`, bif.`stock`, bif.`created_by`, bif.`creation_time`, bif.`updated_by`, bif.`last_update_time`, bif.`revision` from book_info bif, authority auth
and bif.`id` = #{entity.bookInfoId}
and bif.`book_name` like concat('%', #{entity.bookName}, '%')
limit #{filter.pageStart}, #{filter.pageSize}

select
bif.`author`,
from book_info bif where bif.`id` = #{id}
