mapper.select:
select
l.log_id,
l.class_name,
l.method,
l.payload,
l.return_value,
l.elapsed_time,
l.run_time,
l.create_time,
l.create_by
from get_log l


        l.log_id = #{entity.logId}



            l.create_by = #{entity.createBy}


            and l.method = #{entity.method}


            and l.elapsed_time between #{minElapsedTime} and #{maxElapsedTime}
            and l.run_time between #{minRunTime} and #{maxRunTime}



limit #{pageStart}, #{pageSize}
