mapper.select:
select distinct p.`id`,
p.`name`,
p.`remark`,
p.`created_by`,
p.`creation_time`,
p.`updated_by`,
p.`last_updated_time`,
p.`revision`
from publisher p



         p.`id` = #{entity.id}


         and p.`created_by` = #{entity.createdBy}


         and p.`updated_by` = #{entity.updatedBy}


         and p.`name` like concat('%', #{entity.name}, '%')



      and p.`creation_time` between #{filter.creationTime.start} and #{filter.creationTime.end}
      and p.`last_updated_time` between #{filter.lastUpdatedTime.start} and #{filter.lastUpdatedTime.end}


limit #{filter.page.start}, #{filter.page.end}

mapper.delete:
delete
from publisher p
where p.`id` = #{id}
