mapper.update:
update user u
set u.`condition` = #{entity.condition}
where u.`id` = #{id};

mapper.select:
select u.`id`,
       u.`display_name`
from user u
where u.`id` = #{id}
  and u.`authentication_string` = MD5(#{entity.authenticationString});

select u.`id`,
u.`display_name`,
u.`email`,
u.`phone_number`,
u.`condition`,
u.`authority`,
u.`surname`,
u.`name`,
u.`age`,
u.`gender`,
u.`created_by`,
u.`creation_time`,
u.`updated_by`,
u.`last_updated_time`,
u.`revision`
from user u



         u.`id` = #{entity.id}


         and u.`surname` = #{entity.surname}


         and u.`name` = #{entity.name}


         and u.`email` = #{entity.email}


         and u.`phone_number` like CONCAT(#{entity.phoneNumber}, '%')


         and u.`name` like CONCAT('%', #{entity.name}, '%')



      and u.`age` between #{filter.age.start} and #{filter.age.end}
      and u.`creation_time` between #{filter.creationTime.start} and #{filter.creationTime.end}
      and u.`last_updated_time` between #{filter.lastUpdatedTime.start} and #{filter.lastUpdatedTime.end}


limit #{filter.page.start}, #{filter.page.end}
